// assets/js/main.min.js

// Function to copy contract address
function copyAddress() {
    const addressText = document.querySelector('.contract-address span').innerText.split(': ')[1];
    navigator.clipboard.writeText(addressText).then(() => {
        showCopyNotification();
    }).catch(err => {
        showCopyErrorNotification();
        console.error('Error copying text: ', err);
    });
}

// Notification Functions
function createNotification(message, bgColor = 'var(--notification-bg)') {
    const notification = document.createElement('div');
    notification.innerText = message;
    notification.style.position = 'fixed';
    notification.style.bottom = '20px';
    notification.style.right = '20px';
    notification.style.padding = '10px 20px';
    notification.style.backgroundColor = bgColor;
    notification.style.color = '#ffffff';
    notification.style.borderRadius = '5px';
    notification.style.boxShadow = '0px 0px 10px rgba(0, 0, 0, 0.5)';
    notification.style.zIndex = '1000';
    notification.style.fontSize = '1rem';
    notification.style.opacity = '0.9';
    document.body.appendChild(notification);

    setTimeout(() => {
        notification.remove();
    }, 3000);
}

function showCopyNotification() {
    createNotification('Address Copied!', 'var(--notification-bg)');
}

function showCopyErrorNotification() {
    createNotification('Failed to copy the contract address. Please try manually.', 'var(--error-bg)');
}
